mapper.select:
select
     o.*, u.username, u.avatar as userAvatar
from app_order o
left join app_user u on o.user_id = u.id

    o.is_delete = 0

        and o.sn = #{appOrder.sn}


        and o.order_status = #{appOrder.orderStatus}


        and u.username = #{appOrder.username}


order by o.created desc
